import React, {Component} from 'react'
import SearchInput, {createFilter} from 'react-search-input'

const locations = [];

const KEYS_TO_FILTERS = ['location.name']

export default class Search extends Component {
  constructor (props) {
    super(props)
    this.state = {
      searchTerm: ''
    }
    this.searchUpdated = this.searchUpdated.bind(this)
    console.log(props);
  }

  render () {
    const filteredEmails = locations.filter(createFilter(this.state.searchTerm, KEYS_TO_FILTERS))
 console.log(filteredEmails);
    return (
      <div>
        <SearchInput className="search-input" onChange={this.searchUpdated} />
        {filteredEmails.map(location => {
          return (
            <div className="mail" key={location.id}>
              <div className="from">{location.name}</div>
            </div>
          )
        })}
      </div>
    )
  }

  searchUpdated (term) {
    this.setState({searchTerm: term})
  }
}
