import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
//import Search from '../../components/Search';

function Search() {
  const [searchTerm, setSearchTerm] = React.useState("");
  const [searchResults, setSearchResults] = React.useState([]);
  const handleChange = e => {
    setSearchTerm(e.target.value);
  };
  React.useEffect(() => {
    const results = locations.filter(location =>
      location.toLowerCase().includes(searchTerm)
    );
    setSearchResults(results);
  }, [searchTerm]);
  console.log(searchResults);
  return (
    <div className="App">
      <input
        type="text"
        placeholder="Search locations..."
        value={searchTerm}
        onChange={handleChange}
      />
      <ul>
        {searchResults.map(item => (
          <li key={item.id}>{item}</li>
        ))}
      </ul>
    </div>
  );
}
const Film = props => (
  <Layout>
    <h1>{props.film.title}</h1>
    <p>{props.film.description}</p>
    <Search locations/>
    <h1>LOCATIONS</h1>
    <ul>
      {props.locations.map(location=> (

        <li key={location.id}>

          <Link href="/l/[id]" as={`/l/${location.id}`}>
            <a>Location: {location.name}</a>
          </Link>
        </li>

      ))}
    </ul>
  </Layout>
);

Film.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://ghibliapi.herokuapp.com/films/${id}`);
  const film = await res.json();
  const res2 = await fetch(film.locations);
  const locations = await res2.json();

  console.log(locations);
  //console.log(`Fetched show: ${film.people}`);

  return { film,locations };
};

export default Film;
