import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import Search from '../../components/Search';

const locations1 = [
  "S",
  "Ams",
  "Gro",
  "F",
  "T",
  "Lixe",
  "San",
  "Amstel"
];
//console.log(props.locations.name);

const Film = props => (
  <Layout>
    <h1>{props.film.title}</h1>
    <p>{props.film.description}</p>
    <Search locations={props.locationsNames}/>
    <h1>LOCATIONS</h1>
    <ul>
      {props.locations.map(location=> (

        <li key={location.id}>

          <Link href="/l/[id]" as={`/l/${location.id}`}>
            <a>Location: {location.name}</a>
          </Link>
        </li>

      ))}
    </ul>
  </Layout>
);

Film.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://ghibliapi.herokuapp.com/films/${id}`);
  const film = await res.json();
  const res2 = await fetch(film.locations);
  const locations = await res2.json();

  //let locationsNames = locations.map(item => item.name);
  let locationsNames = locations.map(item =>({ value: person.id, text: person.name }))
  //console.log(`Fetched show: ${film.people}`);
  console.log(locationsNames)

  return { film,locations,locationsNames };
};

export default Film;
