import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Film = props => (
  <Layout>
    <h1>{props.film.title}</h1>
    <p>{props.film.description}</p>
    <h1>PEOPLE</h1>
    <ul>
      {props.film2.map(film => (

        <li key={film.id}>
          <h2>{film.title}</h2>
          <p>Release Date: { film.release_date}</p>
          <p>Score: { film. rt_score}</p>
          <Link href="/p/[id]" as={`/p/${film.id}`}>
            <a>More Info</a>
          </Link>
        </li>

      ))}
    </ul>
  </Layout>
);

Film.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://ghibliapi.herokuapp.com/films/${id}`);
  const film = await res.json();
  const res2 = await fetch(film.people);
  const actors = await res2.json();

  console.log(film2);
  //console.log(`Fetched show: ${film.people}`);

  return { film,actors };
};

export default Film;
